: ${M4_DEPENDENCIES:=}

#-------------------------------------------------------------------------------
install_m4()
#-------------------------------------------------------------------------------
{
  info "Installing m4"

  #-----------------------------------------------------------------------------
  rosesh__install_dep_setup || exit 1
  #-----------------------------------------------------------------------------

  #-----------------------------------------------------------------------------
  # Dependencies
  #-----------------------------------------------------------------------------
  install_deps ${M4_DEPENDENCIES} || exit 1

  #-----------------------------------------------------------------------------
  # Installation
  #-----------------------------------------------------------------------------
  set -x
  #-----------------------------------------------------------------------------
  if [ ! -f "${ROSE_SH_PREFIX}/include/m4.h" ]; then
      mkdir -p "m4"  || exit 1
      cd "m4/"    || exit 1

      tar xzvf "${DEPENDENCIES_DIR}/m4-1.4.17.tar.gz" || exit 1
      cd "m4-1.4.17/" || exit 1

      ./configure \
          --prefix="$ROSE_SH_PREFIX" \
          --libdir="$ROSE_SH_LIBDIR"

      make -j${parallelism} || exit 1
      make -j${parallelism} install || exit 1
  else
      info "[SKIP] m4 is already installed"
  fi
  #-----------------------------------------------------------------------------
  set +x
  #-----------------------------------------------------------------------------

  #-----------------------------------------------------------------------------
  rosesh__install_dep_teardown || exit 1
  #-----------------------------------------------------------------------------
}
